    public void totalOtros() {
        double gaseosa = 0;
        double agua = 0;
        double refresco = 0;
        double ensalada = 0;
        double hamburguesa = 0;
        double pizza = 0;
        double yogurt = 0;
        double papas = 0;
        if (this.txtgaseosa.getText().equals("")) {
            gaseosa = 0;
        } else if (!this.txtgaseosa.getText().equals("")) {
            gaseosa = Double.parseDouble(txtgaseosa.getText());
        }

        if (this.txtagua.getText().equals("")) {
            agua = 0;
        } else if (!this.txtagua.getText().equals("")) {
            agua = Double.parseDouble(txtagua.getText());
        }
        if (this.txtrefresco.getText().equals("")) {
            refresco = 0;
        } else if (!this.txtrefresco.getText().equals("")) {
            refresco = Double.parseDouble(txtrefresco.getText());
        }
        if (this.txtensalada.getText().equals("")) {
            ensalada = 0;
        } else if (!this.txtensalada.getText().equals("")) {
            ensalada = Double.parseDouble(txtensalada.getText());
        }
        if (this.txthamburguesa.getText().equals("")) {
            hamburguesa = 0;
        } else if (!this.txthamburguesa.getText().equals("")) {
            hamburguesa = Double.parseDouble(txthamburguesa.getText());
        }
        if (this.txtpizza.getText().equals("")) {
            pizza = 0;
        } else if (!this.txtpizza.getText().equals("")) {
            pizza = Double.parseDouble(txtpizza.getText());
        }
        if (this.txtyogurt.getText().equals("")) {
            yogurt = 0;
        } else if (!this.txtyogurt.getText().equals("")) {
            yogurt = Double.parseDouble(txtyogurt.getText());
        }
        if (this.txtpapas.getText().equals("")) {
            papas = 0;
        } else if (!this.txtpapas.getText().equals("")) {
            papas = Double.parseDouble(txtpapas.getText());
        }
        total = (gaseosa * 0.60) + (agua * 0.60) + (ensalada * 3.00) + (hamburguesa * 2.50) + (yogurt * 0.50) + (refresco * 0.50) + (papas * 0.50) + (pizza * 4);

    }

